import React, { useState, useEffect } from "react";

export default function TotalExpense(props) {
  const [totalIncome, setTotalIncome] = useState(0);
  let [totalOutcome, setTotalOutcome] = useState(0);

  console.log(props.items);
  console.log(props.year);
  props.type.forEach((element) => {
    console.log(element.type);
  });

  useEffect(() => {
    let total = { income: 0, outcome: 0 };
    if (
      props.items.forEach((item) => {
        if (item.type === "지출") {
          total.outcome += item.amount;
        } else {
          total.income += item.amount;
        }
      })
    );
    setTotalOutcome(total.outcome);
    setTotalIncome(total.income);
  });

  return (
    <>
      <h1>나의 자산 현황</h1>
      <h2>{totalOutcome}</h2>
      <h2>{totalIncome}</h2>
    </>
  );
}
