export default function DataChart(props) {
  props.data.forEach((element) => {
    console.log(element.amount);
  });

  const data = {
    labels: [
      "1월",
      "2월",
      "3월",
      "4월",
      "5월",
      "6월",
      "7월",
      "8월",
      "9월",
      "10월",
      "11월",
      "12월",
    ],
    datasets: [
      {
        type: "bar",
        label: "수입",
        backgroundColor: "red",
        data: props.amount,
      },
      {
        type: "bar",
        label: "지출",
        backgroundColor: "blue",
        data: props.amount,
      },
    ],
  };
  return (
    <div>
      <Line type="bar" data={data} />
    </div>
  );
}
